import axios from 'axios';

// 创建axios实例
const api = axios.create({
  baseURL: 'http://localhost:8000',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// 请求拦截器 - 添加userId作为认证
api.interceptors.request.use(
  (config) => {
    const userId = localStorage.getItem('userId');
    if (userId) {
      config.headers.Authorization = `Bearer ${userId}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 响应拦截器 - 处理错误
api.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    if (error.response) {
      switch (error.response.status) {
        case 401:
          localStorage.removeItem('userId');
          window.location.href = '/login';
          break;
        case 403:
          console.error('没有权限访问');
          break;
        case 404:
          console.error('请求的资源不存在');
          break;
        case 500:
          console.error('服务器错误');
          break;
        default:
          console.error('请求失败:', error.response.data);
      }
    } else {
      console.error('网络错误:', error.message);
    }
    return Promise.reject(error);
  }
);

// 知识点管理API
export const knowledgePointAPI = {
  // 获取所有知识点
  getAll: (params) => api.get('/api/knowledge-points', { params }),

  // 根据ID获取知识点
  getById: (id) => api.get(`/api/knowledge-points/${id}`),

  // 创建知识点
  create: (data) => api.post('/api/knowledge-points', data),

  // 更新知识点
  update: (id, data) => api.put(`/api/knowledge-points/${id}`, data),

  // 删除知识点
  delete: (id) => api.delete(`/api/knowledge-points/${id}`),

  // 获取科目列表
  getSubjects: () => api.get('/api/knowledge-points/subjects/list'),

  // 获取分类列表
  getCategories: () => api.get('/api/knowledge-points/categories/list'),

  // 获取重要度列表
  getImportance: () => api.get('/api/knowledge-points/importances/list'),

  // 获取难度列表
  getDifficulty: () => api.get('/api/knowledge-points/difficulties/list'),
};

// 用户认证API
export const authAPI = {
  // 登录
  login: (data) => api.post('/api/login', data),

  // 注册
  register: (data) => api.post('/api/register', data),

  // 获取用户信息
  getUserInfo: () => api.get('/api/user/info'),
};

export default api;
