import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './KnowledgeManagement.css';

const KnowledgeManagement = () => {
  const navigate = useNavigate();
  const [userId, setUserId] = useState(null);
  const [knowledgePoints, setKnowledgePoints] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingPoint, setEditingPoint] = useState(null);
  const [filters, setFilters] = useState({
    subject: '',
    category: '',
    importance: '',
    difficulty: ''
  });

  // 表单数据
  const [formData, setFormData] = useState({
    subject: '',
    point_name: '',
    category: '',
    importance: '中',
    difficulty: '中',
    exam_points: '',
    content: ''
  });

  useEffect(() => {
    // 从localStorage获取userId
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(parseInt(storedUserId));
      fetchKnowledgePoints(parseInt(storedUserId));
    }
  }, []);

  // 获取知识点列表
  const fetchKnowledgePoints = async (uid, filterParams = {}) => {
    try {
      const queryParams = new URLSearchParams(filterParams).toString();
      const response = await fetch(`http://localhost:8000/knowledge-point/${uid}${queryParams ? '?' + queryParams : ''}`);
      if (response.ok) {
        const data = await response.json();
        setKnowledgePoints(data);
      }
    } catch (error) {
      console.error('获取知识点列表失败:', error);
    }
  };

  // 处理筛选
  const handleFilter = () => {
    const filterParams = {};
    if (filters.subject) filterParams.subject = filters.subject;
    if (filters.category) filterParams.category = filters.category;
    if (filters.importance) filterParams.importance = filters.importance;
    if (filters.difficulty) filterParams.difficulty = filters.difficulty;

    fetchKnowledgePoints(userId, filterParams);
  };

  // 重置筛选
  const handleResetFilter = () => {
    setFilters({
      subject: '',
      category: '',
      importance: '',
      difficulty: ''
    });
    fetchKnowledgePoints(userId);
  };

  // 打开添加模态框
  const handleAdd = () => {
    setEditingPoint(null);
    setFormData({
      subject: '',
      point_name: '',
      category: '',
      importance: '中',
      difficulty: '中',
      exam_points: '',
      content: ''
    });
    setShowModal(true);
  };

  // 打开编辑模态框
  const handleEdit = (point) => {
    setEditingPoint(point);
    setFormData({
      subject: point.subject,
      point_name: point.point_name,
      category: point.category,
      importance: point.importance,
      difficulty: point.difficulty,
      exam_points: point.exam_points || '',
      content: point.content || ''
    });
    setShowModal(true);
  };

  // 删除知识点
  const handleDelete = async (kpId) => {
    if (window.confirm('确定要删除这个知识点吗？')) {
      try {
        const response = await fetch(`http://localhost:8000/knowledge-point/${kpId}`, {
          method: 'DELETE'
        });
        if (response.ok) {
          fetchKnowledgePoints(userId);
          alert('删除成功');
        }
      } catch (error) {
        console.error('删除失败:', error);
        alert('删除失败');
      }
    }
  };

  // 保存知识点
  const handleSave = async (e) => {
    e.preventDefault();

    if (!formData.subject || !formData.point_name || !formData.category) {
      alert('请填写必填字段');
      return;
    }

    try {
      const url = editingPoint
        ? `http://localhost:8000/knowledge-point/${editingPoint.kp_id}`
        : 'http://localhost:8000/knowledge-point/';

      const method = editingPoint ? 'PUT' : 'POST';
      const payload = editingPoint
        ? formData
        : { ...formData, id: userId };

      const response = await fetch(url, {
        method: method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(payload)
      });

      if (response.ok) {
        setShowModal(false);
        fetchKnowledgePoints(userId);
        alert(editingPoint ? '更新成功' : '添加成功');
      } else {
        const errorData = await response.json();
        alert(errorData.detail || '操作失败');
      }
    } catch (error) {
      console.error('保存失败:', error);
      alert('保存失败');
    }
  };

  const handleBack = () => {
    navigate('/study-assistant');
  };

  if (!userId) {
    return (
      <div className="knowledge-management-container">
        <div className="header">
          <button className="back-button" onClick={handleBack}>
            ← 返回
          </button>
          <h1>知识点管理</h1>
        </div>
        <div className="content">
          <p>请先登录</p>
        </div>
      </div>
    );
  }

  return (
    <div className="knowledge-management-container">
      <div className="header">
        <button className="back-button" onClick={handleBack}>
          ← 返回
        </button>
        <h1>知识点管理</h1>
        <button className="add-button" onClick={handleAdd}>
          + 添加知识点
        </button>
      </div>

      {/* 筛选区域 */}
      <div className="filter-section">
        <div className="filter-row">
          <div className="filter-item">
            <label>科目：</label>
            <input
              type="text"
              value={filters.subject}
              onChange={(e) => setFilters({ ...filters, subject: e.target.value })}
              placeholder="输入科目"
            />
          </div>
          <div className="filter-item">
            <label>分类：</label>
            <input
              type="text"
              value={filters.category}
              onChange={(e) => setFilters({ ...filters, category: e.target.value })}
              placeholder="输入分类"
            />
          </div>
          <div className="filter-item">
            <label>重要度：</label>
            <select
              value={filters.importance}
              onChange={(e) => setFilters({ ...filters, importance: e.target.value })}
            >
              <option value="">全部</option>
              <option value="低">低</option>
              <option value="中">中</option>
              <option value="高">高</option>
              <option value="必考">必考</option>
            </select>
          </div>
          <div className="filter-item">
            <label>难度：</label>
            <select
              value={filters.difficulty}
              onChange={(e) => setFilters({ ...filters, difficulty: e.target.value })}
            >
              <option value="">全部</option>
              <option value="易">易</option>
              <option value="较易">较易</option>
              <option value="中">中</option>
              <option value="较难">较难</option>
              <option value="难">难</option>
            </select>
          </div>
        </div>
        <div className="filter-buttons">
          <button className="filter-button" onClick={handleFilter}>筛选</button>
          <button className="reset-button" onClick={handleResetFilter}>重置</button>
        </div>
      </div>

      {/* 知识点列表 */}
      <div className="knowledge-list">
        {knowledgePoints.length === 0 ? (
          <div className="empty-state">
            <p>暂无知识点，点击"添加知识点"开始添加</p>
          </div>
        ) : (
          knowledgePoints.map((point) => (
            <div key={point.kp_id} className="knowledge-card">
              <div className="card-header">
                <h3>{point.point_name}</h3>
                <div className="card-actions">
                  <button className="edit-button" onClick={() => handleEdit(point)}>编辑</button>
                  <button className="delete-button" onClick={() => handleDelete(point.kp_id)}>删除</button>
                </div>
              </div>
              <div className="card-body">
                <div className="info-row">
                  <span className="label">科目：</span>
                  <span className="value">{point.subject}</span>
                </div>
                <div className="info-row">
                  <span className="label">分类：</span>
                  <span className="value">{point.category}</span>
                </div>
                <div className="info-row">
                  <span className="label">重要度：</span>
                  <span className={`value importance-${point.importance}`}>{point.importance}</span>
                </div>
                <div className="info-row">
                  <span className="label">难度：</span>
                  <span className={`value difficulty-${point.difficulty}`}>{point.difficulty}</span>
                </div>
                {point.exam_points && (
                  <div className="info-row">
                    <span className="label">考点：</span>
                    <span className="value">{point.exam_points}</span>
                  </div>
                )}
                {point.content && (
                  <div className="info-row">
                    <span className="label">内容：</span>
                    <span className="value content">{point.content}</span>
                  </div>
                )}
                <div className="info-row">
                  <span className="label">创建时间：</span>
                  <span className="value">{point.create_time}</span>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* 添加/编辑模态框 */}
      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{editingPoint ? '编辑知识点' : '添加知识点'}</h2>
              <button className="close-button" onClick={() => setShowModal(false)}>×</button>
            </div>
            <form onSubmit={handleSave} className="modal-body">
              <div className="form-group">
                <label>科目 <span className="required">*</span></label>
                <input
                  type="text"
                  value={formData.subject}
                  onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                  placeholder="如：数据结构、计算机组成原理"
                  required
                />
              </div>
              <div className="form-group">
                <label>知识点名称 <span className="required">*</span></label>
                <input
                  type="text"
                  value={formData.point_name}
                  onChange={(e) => setFormData({ ...formData, point_name: e.target.value })}
                  placeholder="如：二叉树遍历、完形填空技巧"
                  required
                />
              </div>
              <div className="form-group">
                <label>分类 <span className="required">*</span></label>
                <input
                  type="text"
                  value={formData.category}
                  onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                  placeholder="如：算法、语法"
                  required
                />
              </div>
              <div className="form-row">
                <div className="form-group">
                  <label>重要度</label>
                  <select
                    value={formData.importance}
                    onChange={(e) => setFormData({ ...formData, importance: e.target.value })}
                  >
                    <option value="低">低</option>
                    <option value="中">中</option>
                    <option value="高">高</option>
                    <option value="必考">必考</option>
                  </select>
                </div>
                <div className="form-group">
                  <label>难度</label>
                  <select
                    value={formData.difficulty}
                    onChange={(e) => setFormData({ ...formData, difficulty: e.target.value })}
                  >
                    <option value="易">易</option>
                    <option value="较易">较易</option>
                    <option value="中">中</option>
                    <option value="较难">较难</option>
                    <option value="难">难</option>
                  </select>
                </div>
              </div>
              <div className="form-group">
                <label>考点</label>
                <input
                  type="text"
                  value={formData.exam_points}
                  onChange={(e) => setFormData({ ...formData, exam_points: e.target.value })}
                  placeholder="如：选择题、计算题、论述题"
                />
              </div>
              <div className="form-group">
                <label>详细内容</label>
                <textarea
                  value={formData.content}
                  onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                  placeholder="输入知识点的详细内容"
                  rows="6"
                />
              </div>
              <div className="modal-footer">
                <button type="button" className="cancel-button" onClick={() => setShowModal(false)}>
                  取消
                </button>
                <button type="submit" className="submit-button">
                  {editingPoint ? '更新' : '添加'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default KnowledgeManagement;
