import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './KnowledgeManagement.css';


const KnowledgeManagement = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [knowledgePoints, setKnowledgePoints] = useState([]);
  const [subjects, setSubjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // 筛选状态
  const [filterSubject, setFilterSubject] = useState('');
  const [filterPointName, setFilterPointName] = useState('');

  // 模态框状态
  const [showModal, setShowModal] = useState(false);
  const [modalMode, setModalMode] = useState('create'); // 'create' or 'edit'
  const [currentKp, setCurrentKp] = useState({
    subject: '',
    point_name: '',
    point_content: ''
  });
  const [editingKpId, setEditingKpId] = useState(null);

  // 检查用户登录状态
  useEffect(() => {
    const userId = localStorage.getItem('userId');
    const username = localStorage.getItem('username');

    if (!userId || !username) {
      navigate('/login');
      return;
    }

    setUser({ id: parseInt(userId), username });
    fetchKnowledgePoints(parseInt(userId));
    fetchSubjects(parseInt(userId));
  }, [navigate]);

  // 获取知识点列表
  const fetchKnowledgePoints = async (userId) => {
    try {
      setLoading(true);
      const params = new URLSearchParams({ user_id: userId });
      if (filterSubject) params.append('subject', filterSubject);
      if (filterPointName) params.append('point_name', filterPointName);

      const response = await fetch(`http://localhost:8000/api/knowledge-points/?${params}`);

      if (!response.ok) {
        throw new Error('获取知识点列表失败');
      }

      const data = await response.json();
      setKnowledgePoints(data);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // 获取科目列表
  const fetchSubjects = async (userId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/knowledge-points/subjects/list?user_id=${userId}`);

      if (!response.ok) {
        throw new Error('获取科目列表失败');
      }

      const data = await response.json();
      setSubjects(data);
    } catch (err) {
      console.error('获取科目列表失败:', err);
    }
  };

  // 处理筛选
  const handleFilter = () => {
    if (user) {
      fetchKnowledgePoints(user.id);
    }
  };

  // 重置筛选
  const handleResetFilter = () => {
    setFilterSubject('');
    setFilterPointName('');
    if (user) {
      fetchKnowledgePoints(user.id);
    }
  };

  // 打开模态框
  const openModal = (mode, kp = null) => {
    setModalMode(mode);
    if (mode === 'edit' && kp) {
      setCurrentKp({
        subject: kp.subject,
        point_name: kp.point_name,
        point_content: kp.point_content || ''
      });
      setEditingKpId(kp.kp_id);
    } else {
      setCurrentKp({
        subject: '',
        point_name: '',
        point_content: ''
      });
      setEditingKpId(null);
    }
    setShowModal(true);
  };

  // 关闭模态框
  const closeModal = () => {
    setShowModal(false);
    setCurrentKp({
      subject: '',
      point_name: '',
      point_content: ''
    });
    setEditingKpId(null);
  };

  // 处理表单提交
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) return;

    try {
      const url = modalMode === 'create'
        ? 'http://localhost:8000/api/knowledge-points/'
        : `http://localhost:8000/api/knowledge-points/${editingKpId}`;

      const method = modalMode === 'create' ? 'POST' : 'PUT';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...currentKp,
          user_id: user.id
        }),
      });

      if (!response.ok) {
        throw new Error(modalMode === 'create' ? '创建知识点失败' : '更新知识点失败');
      }

      // 刷新列表
      fetchKnowledgePoints(user.id);
      fetchSubjects(user.id);
      closeModal();
    } catch (err) {
      setError(err.message);
    }
  };

  // 删除知识点
  const handleDelete = async (kpId) => {
    if (!user) return;

    if (!window.confirm('确定要删除这个知识点吗？')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:8000/api/knowledge-points/${kpId}?user_id=${user.id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('删除知识点失败');
      }

      // 刷新列表
      fetchKnowledgePoints(user.id);
      fetchSubjects(user.id);
    } catch (err) {
      setError(err.message);
    }
  };

  // 处理输入变化
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentKp(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="knowledge-management">
      <div className="container">
        <div className="header">
          <button className="back-button" onClick={() => navigate('/home')}>
            ← 返回
          </button>
          <h1>知识点管理</h1>
          <button className="add-button" onClick={() => openModal('create')}>
            + 添加知识点
          </button>
        </div>

        {/* 筛选区域 */}
        <div className="filter-section">
          <div className="filter-group">
            <label>科目:</label>
            <select
              value={filterSubject}
              onChange={(e) => setFilterSubject(e.target.value)}
            >
              <option value="">全部科目</option>
              {subjects.map(subject => (
                <option key={subject} value={subject}>{subject}</option>
              ))}
            </select>
          </div>

          <div className="filter-group">
            <label>知识点名称:</label>
            <input
              type="text"
              placeholder="输入知识点名称"
              value={filterPointName}
              onChange={(e) => setFilterPointName(e.target.value)}
            />
          </div>

          <div className="filter-actions">
            <button className="filter-button" onClick={handleFilter}>
              筛选
            </button>
            <button className="reset-button" onClick={handleResetFilter}>
              重置
            </button>
          </div>
        </div>

        {/* 错误提示 */}
        {error && (
          <div className="error-message">
            {error}
            <button onClick={() => setError(null)}>×</button>
          </div>
        )}

        {/* 加载状态 */}
        {loading && (
          <div className="loading">加载中...</div>
        )}

        {/* 知识点列表 */}
        {!loading && (
          <div className="knowledge-points-list">
            {knowledgePoints.length === 0 ? (
              <div className="empty-state">
                <p>暂无知识点数据</p>
                <button onClick={() => openModal('create')}>添加第一个知识点</button>
              </div>
            ) : (
              knowledgePoints.map(kp => (
                <div key={kp.kp_id} className="knowledge-point-card">
                  <div className="kp-header">
                    <span className="kp-subject">{kp.subject}</span>
                    <div className="kp-actions">
                      <button
                        className="edit-btn"
                        onClick={() => openModal('edit', kp)}
                      >
                        编辑
                      </button>
                      <button
                        className="delete-btn"
                        onClick={() => handleDelete(kp.kp_id)}
                      >
                        删除
                      </button>
                    </div>
                  </div>
                  <h3 className="kp-name">{kp.point_name}</h3>
                  {kp.point_content && (
                    <p className="kp-content">{kp.point_content}</p>
                  )}
                  <div className="kp-footer">
                    <span className="kp-date">
                      创建于: {new Date(kp.created_at).toLocaleString('zh-CN')}
                    </span>
                  </div>
                </div>
              ))
            )}
          </div>
        )}

        {/* 模态框 */}
        {showModal && (
          <div className="modal-overlay" onClick={closeModal}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <div className="modal-header">
                <h2>{modalMode === 'create' ? '添加知识点' : '编辑知识点'}</h2>
                <button className="close-button" onClick={closeModal}>×</button>
              </div>

              <form onSubmit={handleSubmit} className="modal-body">
                <div className="form-group">
                  <label htmlFor="subject">科目 *</label>
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    value={currentKp.subject}
                    onChange={handleInputChange}
                    required
                    placeholder="请输入科目"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="point_name">知识点名称 *</label>
                  <input
                    type="text"
                    id="point_name"
                    name="point_name"
                    value={currentKp.point_name}
                    onChange={handleInputChange}
                    required
                    placeholder="请输入知识点名称"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="point_content">知识点内容</label>
                  <textarea
                    id="point_content"
                    name="point_content"
                    value={currentKp.point_content}
                    onChange={handleInputChange}
                    rows="6"
                    placeholder="请输入知识点内容（可选）"
                  />
                </div>

                <div className="modal-footer">
                  <button type="button" className="cancel-button" onClick={closeModal}>
                    取消
                  </button>
                  <button type="submit" className="submit-button">
                    {modalMode === 'create' ? '创建' : '更新'}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default KnowledgeManagement;
