import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './KnowledgeManagement.css';

function KnowledgeManagement() {
    const navigate = useNavigate();
    const [userId, setUserId] = useState(null);
    const [knowledgePoints, setKnowledgePoints] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [editingPoint, setEditingPoint] = useState(null);
    const [formData, setFormData] = useState({
        subject: '',
        point_name: '',
        category: '',
        importance: '中',
        difficulty: '中',
        exam_points: '',
        content: ''
    });
    const [filters, setFilters] = useState({
        subject: '',
        category: '',
        importance: '',
        difficulty: '',
        point_name: ''
    });
    const [subjects, setSubjects] = useState([]);
    const [categories, setCategories] = useState([]);

    // 检查用户登录状态
    useEffect(() => {
        const storedUserId = localStorage.getItem('userId');
        if (!storedUserId) {
            navigate('/login');
            return;
        }
        setUserId(parseInt(storedUserId));
        fetchKnowledgePoints(parseInt(storedUserId));
        fetchSubjects(parseInt(storedUserId));
        fetchCategories(parseInt(storedUserId));
    }, [navigate]);

    // 获取知识点列表
    const fetchKnowledgePoints = async (uid) => {
        try {
            setLoading(true);
            const queryParams = new URLSearchParams();
            if (filters.subject) queryParams.append('subject', filters.subject);
            if (filters.category) queryParams.append('category', filters.category);
            if (filters.importance) queryParams.append('importance', filters.importance);
            if (filters.difficulty) queryParams.append('difficulty', filters.difficulty);
            if (filters.point_name) queryParams.append('point_name', filters.point_name);

            const response = await fetch(`http://localhost:8000/api/knowledge-points/?user_id=${uid}&${queryParams}`);
            if (!response.ok) {
                throw new Error('获取知识点列表失败');
            }
            const data = await response.json();
            setKnowledgePoints(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    // 获取科目列表
    const fetchSubjects = async (uid) => {
        try {
            const response = await fetch(`http://localhost:8000/api/knowledge-points/subjects/list?user_id=${uid}`);
            if (response.ok) {
                const data = await response.json();
                setSubjects(data);
            }
        } catch (err) {
            console.error('获取科目列表失败:', err);
        }
    };

    // 获取分类列表
    const fetchCategories = async (uid) => {
        try {
            const response = await fetch(`http://localhost:8000/api/knowledge-points/categories/list?user_id=${uid}`);
            if (response.ok) {
                const data = await response.json();
                setCategories(data);
            }
        } catch (err) {
            console.error('获取分类列表失败:', err);
        }
    };

    // 打开模态框
    const openModal = (point = null) => {
        if (point) {
            setEditingPoint(point);
            setFormData({
                subject: point.subject,
                point_name: point.point_name,
                category: point.category,
                importance: point.importance,
                difficulty: point.difficulty,
                exam_points: point.exam_points || '',
                content: point.content || ''
            });
        } else {
            setEditingPoint(null);
            setFormData({
                subject: '',
                point_name: '',
                category: '',
                importance: '中',
                difficulty: '中',
                exam_points: '',
                content: ''
            });
        }
        setShowModal(true);
    };

    // 关闭模态框
    const closeModal = () => {
        setShowModal(false);
        setEditingPoint(null);
        setFormData({
            subject: '',
            point_name: '',
            category: '',
            importance: '中',
            difficulty: '中',
            exam_points: '',
            content: ''
        });
    };

    // 处理表单提交
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const url = editingPoint
                ? `http://localhost:8000/api/knowledge-points/${editingPoint.kp_id}?user_id=${userId}`
                : `http://localhost:8000/api/knowledge-points/?user_id=${userId}`;

            const method = editingPoint ? 'PUT' : 'POST';

            const response = await fetch(url, {
                method: method,
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                throw new Error(editingPoint ? '更新知识点失败' : '创建知识点失败');
            }

            closeModal();
            fetchKnowledgePoints(userId);
            fetchSubjects(userId);
            fetchCategories(userId);
        } catch (err) {
            setError(err.message);
        }
    };

    // 删除知识点
    const handleDelete = async (kpId) => {
        if (!window.confirm('确定要删除这个知识点吗？')) {
            return;
        }

        try {
            const response = await fetch(`http://localhost:8000/api/knowledge-points/${kpId}?user_id=${userId}`, {
                method: 'DELETE',
            });

            if (!response.ok) {
                throw new Error('删除知识点失败');
            }

            fetchKnowledgePoints(userId);
            fetchSubjects(userId);
            fetchCategories(userId);
        } catch (err) {
            setError(err.message);
        }
    };

    // 处理筛选变化
    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    // 应用筛选
    const applyFilters = () => {
        fetchKnowledgePoints(userId);
    };

    // 重置筛选
    const resetFilters = () => {
        setFilters({
            subject: '',
            category: '',
            importance: '',
            difficulty: '',
            point_name: ''
        });
        fetchKnowledgePoints(userId);
    };

    if (loading) {
        return <div className="loading">加载中...</div>;
    }

    if (error) {
        return <div className="error">错误: {error}</div>;
    }

    return (
        <div className="knowledge-management">
            <button className="back-button" onClick={() => navigate('/study-assistant')}>
                返回
            </button>

            <h1 className="page-title">知识点管理</h1>

            <div className="filters-section">
                <div className="filter-row">
                    <select
                        name="subject"
                        value={filters.subject}
                        onChange={handleFilterChange}
                        className="filter-select"
                    >
                        <option value="">全部科目</option>
                        {subjects.map(subject => (
                            <option key={subject} value={subject}>{subject}</option>
                        ))}
                    </select>

                    <select
                        name="category"
                        value={filters.category}
                        onChange={handleFilterChange}
                        className="filter-select"
                    >
                        <option value="">全部分类</option>
                        {categories.map(category => (
                            <option key={category} value={category}>{category}</option>
                        ))}
                    </select>

                    <select
                        name="importance"
                        value={filters.importance}
                        onChange={handleFilterChange}
                        className="filter-select"
                    >
                        <option value="">全部重要度</option>
                        <option value="低">低</option>
                        <option value="中">中</option>
                        <option value="高">高</option>
                        <option value="必考">必考</option>
                    </select>

                    <select
                        name="difficulty"
                        value={filters.difficulty}
                        onChange={handleFilterChange}
                        className="filter-select"
                    >
                        <option value="">全部难度</option>
                        <option value="易">易</option>
                        <option value="较易">较易</option>
                        <option value="中">中</option>
                        <option value="较难">较难</option>
                        <option value="难">难</option>
                    </select>

                    <input
                        type="text"
                        name="point_name"
                        value={filters.point_name}
                        onChange={handleFilterChange}
                        placeholder="搜索知识点名称"
                        className="filter-input"
                    />

                    <button onClick={applyFilters} className="filter-button">筛选</button>
                    <button onClick={resetFilters} className="filter-button reset">重置</button>
                </div>
            </div>

            <button onClick={() => openModal()} className="add-button">
                + 添加知识点
            </button>

            <div className="knowledge-points-list">
                {knowledgePoints.length === 0 ? (
                    <div className="empty-state">暂无知识点，点击上方按钮添加</div>
                ) : (
                    knowledgePoints.map(point => (
                        <div key={point.kp_id} className="knowledge-point-card">
                            <div className="point-header">
                                <h3 className="point-name">{point.point_name}</h3>
                                <div className="point-actions">
                                    <button onClick={() => openModal(point)} className="edit-button">编辑</button>
                                    <button onClick={() => handleDelete(point.kp_id)} className="delete-button">删除</button>
                                </div>
                            </div>
                            <div className="point-info">
                                <span className="point-subject">{point.subject}</span>
                                <span className="point-category">{point.category}</span>
                                <span className={`point-importance ${point.importance}`}>{point.importance}</span>
                                <span className={`point-difficulty ${point.difficulty}`}>{point.difficulty}</span>
                            </div>
                            {point.exam_points && (
                                <div className="point-exam-points">
                                    <strong>考点：</strong>{point.exam_points}
                                </div>
                            )}
                            {point.content && (
                                <div className="point-content">
                                    <strong>内容：</strong>{point.content}
                                </div>
                            )}
                            <div className="point-footer">
                                <span className="point-time">创建时间: {new Date(point.create_time).toLocaleString()}</span>
                            </div>
                        </div>
                    ))
                )}
            </div>

            {showModal && (
                <div className="modal-overlay" onClick={closeModal}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>{editingPoint ? '编辑知识点' : '添加知识点'}</h2>
                            <button onClick={closeModal} className="modal-close">×</button>
                        </div>
                        <form onSubmit={handleSubmit} className="modal-form">
                            <div className="form-group">
                                <label>科目 *</label>
                                <input
                                    type="text"
                                    value={formData.subject}
                                    onChange={(e) => setFormData({...formData, subject: e.target.value})}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>知识点名称 *</label>
                                <input
                                    type="text"
                                    value={formData.point_name}
                                    onChange={(e) => setFormData({...formData, point_name: e.target.value})}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>分类 *</label>
                                <input
                                    type="text"
                                    value={formData.category}
                                    onChange={(e) => setFormData({...formData, category: e.target.value})}
                                    required
                                />
                            </div>
                            <div className="form-row">
                                <div className="form-group">
                                    <label>重要度</label>
                                    <select
                                        value={formData.importance}
                                        onChange={(e) => setFormData({...formData, importance: e.target.value})}
                                    >
                                        <option value="低">低</option>
                                        <option value="中">中</option>
                                        <option value="高">高</option>
                                        <option value="必考">必考</option>
                                    </select>
                                </div>
                                <div className="form-group">
                                    <label>难度</label>
                                    <select
                                        value={formData.difficulty}
                                        onChange={(e) => setFormData({...formData, difficulty: e.target.value})}
                                    >
                                        <option value="易">易</option>
                                        <option value="较易">较易</option>
                                        <option value="中">中</option>
                                        <option value="较难">较难</option>
                                        <option value="难">难</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-group">
                                <label>考点</label>
                                <input
                                    type="text"
                                    value={formData.exam_points}
                                    onChange={(e) => setFormData({...formData, exam_points: e.target.value})}
                                    placeholder="如：选择题、计算题、论述题"
                                />
                            </div>
                            <div className="form-group">
                                <label>详细内容</label>
                                <textarea
                                    value={formData.content}
                                    onChange={(e) => setFormData({...formData, content: e.target.value})}
                                    rows="5"
                                    placeholder="输入知识点的详细内容..."
                                />
                            </div>
                            <div className="modal-footer">
                                <button type="button" onClick={closeModal} className="modal-button cancel">取消</button>
                                <button type="submit" className="modal-button submit">
                                    {editingPoint ? '更新' : '添加'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

export default KnowledgeManagement;
